import React, { useState, useRef } from 'react';

function SearchSection() {
  const [showSearchSection, setShowSearchSection] = useState(false);
  const contentRef = useRef(null);

  const toggleSearchSection = () => {
    setShowSearchSection(!showSearchSection);
  };

  const contentStyle = {
    height: showSearchSection ? contentRef.current.scrollHeight + 'px' : '0',
  };

  return (
    <div>
      <button
        className={`fixed bottom-0 left-0 right-0 text-white py-2 px-4 rounded-lg sm:hidden z-10 rounded-xl bg-gray-100 h-16`}
        onClick={toggleSearchSection}
      >
        {showSearchSection ? (
          <button onClick={toggleSearchSection}>Close</button>
        ) : (
          <button onClick={toggleSearchSection}>Expand</button>
        )}
      </button>

      <div
        className={`fixed bottom-0 left-0 right-0 bg-white p-4 sm:hidden z-10 transition-height overflow-hidden`}
        style={contentStyle}
        ref={contentRef}
      >
        {/* Close button to hide the search section */}
        <button
          className="absolute top-2 right-2 text-gray-600 hover:text-gray-800"
          onClick={toggleSearchSection}
        >
          Close
        </button>
        {/* Content of the SearchSection */}
        <div>Section 1</div>
        <div>Section 2</div>
        <div>Section 3</div>
        <div>Section 4</div>
        <div>Section 5</div>
      </div>
    </div>
  );
}

export default SearchSection;
