import { useRestaurant } from './WifiContext';





export default function SearchStatusComponent() {
  const { searchLocation, isSearching } = useRestaurant();
  return (
    <div className="absolute -translate-y-1/2 bg-white p-4 border rounded shadow-md z-100000 mx-">
      <p>Searching in {searchLocation}</p>
      {isSearching ? <p>Searching for restaurants...</p> : <p>Search complete</p>}
    </div>
  );
}
