import React, { useState } from 'react';
import 'animate.css';




function SearchSection() {
  const [showSearchSection, setShowSearchSection] = useState(false);

  // Function to toggle the visibility of the search section
  const toggleSearchSection = () => {
    setShowSearchSection(!showSearchSection);
  };

  // Function to close the search section
  const closeSearchSection = () => {
    setShowSearchSection(false);
  };

  return (
    <div>
      <button
        className="fixed bottom-0 left-0 right-0  text-white py-2 px-4 rounded-lg sm:hidden z-10 rounded-xl bg-gray-100 h-16"
        onClick={toggleSearchSection}
      >
<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 16 16"><path fill="currentColor" d="M0 7h16v1H0V7z"/></svg>      

</button>

      {/* Display the SearchSection as a sliding section on smaller screens */}
      {showSearchSection && (
        <div className="fixed bottom-0 left-0 right-0 bg-white p-4 sm:hidden animate-slide-up z-10">
          {/* Close button to hide the search section */}
          <button
            className="absolute top-2 right-2 text-gray-600 hover:text-gray-800"
            onClick={closeSearchSection}
          >
            Close
          </button>
          {/* Content of the SearchSection */}
          <div>Section 1</div>
          <div>Section 2</div>
          <div>Section 3</div>
        </div>
      )}

      {/* Display the SearchSection as a permanent section on larger screens */}
      <div className="bg-white p-4 hidden sm:block">
        {/* Content of the SearchSection */}
        <div>Section 1</div>
        <div>Section 2</div>
        <div>Section 3</div>
      </div>
    </div>
  );
}

export default SearchSection;
