  };

  return (
    <div>
      <div onClick={toggleSearchSection}
        className={`fixed bottom-0 left-0 right-0 bg-white p-4 sm:hidden z-10  h-1/2 transition-transform transform${
            showSearchSection ? ' translate-y-0	' : ' translate-y-3/4	'
        }` }
      >
        <button
          className={`absolute top-2 right-2 text-gray-600 hover:text-gray-800 ${
            showSearchSection ? '' : 'hidden'
