import React from 'react';
import { MapContainer, TileLayer,Marker,Popup } from 'react-leaflet';
import L from 'leaflet';

import "leaflet/dist/leaflet.css"

const sanFranciscoBounds = new L.LatLngBounds(
    new L.LatLng(37.615, -123.173), // Southwest corner
    new L.LatLng(37.856, -122.362)  // Northeast corner
  );
  const initialCoordinates = [37.7749, -122.4194];
  const mapOptions = {
    center: initialCoordinates,
    zoom: 12,
    dragging: false, // Make the map undraggable
    zoomControl: false, // Disable zoom control (optional)
  };
  function SanFranciscoMap() {
    return (
        <div><MapContainer  center={initialCoordinates} zoom = {12} className = {'h-screen w-screen'} maxBounds={sanFranciscoBounds}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[51.505, -0.09]}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer></div>

    );
  }

  export default SanFranciscoMap;
